css:
.editor.react .underlayer {
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  z-index: -2;
}
.editor.react .lines {
  min-width: 100%;
}
.editor.react .cursor {
  z-index: 2;
}
.editor.react.is-focused .cursors.blink-off .cursor {
  visibility: hidden;
}
.editor.react .horizontal-scrollbar {
  position: absolute;
  left: 0;
  right: 0;
  bottom: 0;
  height: 15px;
  overflow-x: auto;
  overflow-y: hidden;
  z-index: 3;
}
.editor.react .horizontal-scrollbar .scrollbar-content {
  height: 15px;
}
.editor.react .vertical-scrollbar {
  overflow-x: hidden;
}
.editor.react .scrollbar-corner {
  position: absolute;
  overflow: auto;
  bottom: 0;
  right: 0;
}
.editor.react .scroll-view {
  overflow: hidden;
  z-index: 0;
}
.editor.react .scroll-view-content {
  position: relative;
  width: 100%;
}
.editor.react .gutter .line-number {
  white-space: nowrap;
  padding: 0 .5em;
}
.editor.react .gutter .line-number .icon-right {
  padding: 0;
  padding-left: .1em;
}
.editor {
  overflow: hidden;
  cursor: text;
  display: -webkit-flex;
  -webkit-user-select: none;
  position: relative;
  z-index: 0;
  font-family: Inconsolata, Monaco, Consolas, 'Courier New', Courier;
  line-height: 1.3;
}
.editor .gutter .line-number.cursor-line {
  opacity: 1;
}
.editor .gutter {
  overflow: hidden;
  text-align: right;
  cursor: default;
  min-width: 1em;
  box-sizing: border-box;
}
.editor .gutter .line-number {
  padding-left: .5em;
  opacity: 0.6;
}
.editor .gutter .line-numbers {
  position: relative;
}
.editor .gutter .line-number.folded.cursor-line {
  opacity: 1;
}
.editor .gutter .line-number .icon-right {
  display: inline-block;
  visibility: hidden;
  padding-left: .1em;
  padding-right: .5em;
  opacity: .7;
}
.editor .gutter .line-number .icon-right:before {
  font-family: 'Octicons Regular';
  font-weight: normal;
  font-style: normal;
  display: inline-block;
  line-height: 1;
  -webkit-font-smoothing: antialiased;
  text-decoration: none;
  font-size: 0.8em;
  width: 0.8em;
  height: 0.8em;
  content: "\f0a3";
}
.editor .gutter:hover .line-number.foldable .icon-right {
  visibility: visible;
}
.editor .gutter:hover .line-number.foldable .icon-right:before {
  content: "\f0a3";
}
.editor .gutter:hover .line-number.foldable .icon-right:hover {
  opacity: 1;
}
.editor .gutter .line-number.folded .icon-right,
.editor .gutter:hover .line-number.folded .icon-right {
  visibility: visible;
}
.editor .gutter .line-number.folded .icon-right:before,
.editor .gutter:hover .line-number.folded .icon-right:before {
  font-family: 'Octicons Regular';
  font-weight: normal;
  font-style: normal;
  display: inline-block;
  line-height: 1;
  -webkit-font-smoothing: antialiased;
  text-decoration: none;
  font-size: 0.8em;
  width: 0.8em;
  height: 0.8em;
  content: "\f078";
}
.editor .gutter .line-number.folded .icon-right:before,
.editor .gutter:hover .line-number.folded .icon-right:before {
  position: relative;
  left: -0.1em;
  content: "\f078";
}
.editor .fold-marker:after {
  font-family: 'Octicons Regular';
  font-weight: normal;
  font-style: normal;
  display: inline;
  line-height: 1;
  -webkit-font-smoothing: antialiased;
  text-decoration: none;
  font-size: 0.8em;
  width: 0.8em;
  height: 0.8em;
  content: "\f09a";
  padding-left: 0.2em;
}
.editor .line.cursor-line .fold-marker:after {
  opacity: 1;
}
.editor.is-blurred .line.cursor-line {
  background: rgba(0, 0, 0, 0);
}
.editor .invisible-character {
  font-weight: normal !important;
  font-style: normal !important;
}
.editor .indent-guide {
  display: inline-block;
  box-shadow: inset 1px 0;
}
.editor .vertical-scrollbar {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  width: 15px;
  overflow-y: auto;
  z-index: 3;
}
.editor .scroll-view {
  overflow-x: auto;
  overflow-y: hidden;
  -webkit-flex: 1;
  min-width: 0;
  position: relative;
}
.editor.soft-wrap .scroll-view {
  overflow-x: hidden;
}
.editor .underlayer {
  z-index: 0;
  position: absolute;
  min-height: 100%;
}
.editor .lines {
  position: relative;
  z-index: 1;
}
.editor .overlayer {
  z-index: 2;
  position: absolute;
}
.editor .line {
  white-space: pre;
}
.editor .line span {
  vertical-align: top;
}
.editor .cursor {
  position: absolute;
  border-left: 1px solid;
}
.editor .cursor,
.editor.is-focused .cursor.blink-off {
  visibility: hidden;
}
.editor.is-focused .cursor {
  visibility: visible;
}
.cursor.hidden-cursor {
  display: none;
}
.editor .hidden-input {
  padding: 0;
  border: 0;
  position: absolute;
  z-index: -1;
  top: 0;
  left: 0;
  opacity: 0;
  width: 1px;
}
.editor .selection .region {
  position: absolute;
  pointer-events: none;
  z-index: -1;
}
.editor.mini {
  height: auto;
  line-height: 25px;
}
.editor.mini .cursor {
  width: 2px;
  line-height: 20px;
  margin-top: 2px;
}
.editor.mini .gutter {
  display: none;
}
.editor.mini .scroll-view {
  overflow: hidden;
}
.editor.mini .placeholder-text {
  color: #999999;
}
