(function() {
  var BracketMatcher, BracketMatcherView;

  BracketMatcher = require('./bracket-matcher');

  BracketMatcherView = require('./bracket-matcher-view');

  module.exports = {
    configDefaults: {
      autocompleteBrackets: true,
      autocompleteSmartQuotes: true,
      wrapSelectionsInBrackets: true
    },
    activate: function() {
      return atom.workspaceView.eachEditorView(function(editorView) {
        if (editorView.attached && (editorView.getPane() != null)) {
          new BracketMatcherView(editorView);
          return new BracketMatcher(editorView);
        }
      });
    }
  };

}).call(this);
