(function() {
  var FeedbackStatusView, FeedbackView;

  FeedbackStatusView = require('./feedback-status-view');

  FeedbackView = null;

  module.exports = {
    activate: function() {
      this.feedbackStatusView = new FeedbackStatusView();
      return atom.workspaceView.command('feedback:show', function() {
        if (FeedbackView == null) {
          FeedbackView = require('./feedback-view');
        }
        return new FeedbackView();
      });
    },
    deactivate: function() {
      return this.feedbackStatusView.remove();
    }
  };

}).call(this);
