(function() {
  var createGrammarListView, createGrammarStatusView;

  module.exports = {
    configDefaults: {
      showOnRightSideOfStatusBar: false
    },
    activate: function() {
      atom.workspaceView.command('grammar-selector:show', createGrammarListView);
      return atom.packages.once('activated', createGrammarStatusView);
    }
  };

  createGrammarListView = function() {
    var GrammarListView, editor, view;
    editor = atom.workspace.getActiveEditor();
    if (editor != null) {
      GrammarListView = require('./grammar-list-view');
      view = new GrammarListView(editor);
      return view.attach();
    }
  };

  createGrammarStatusView = function() {
    var GrammarStatusView, statusBar, view;
    statusBar = atom.workspaceView.statusBar;
    if (statusBar != null) {
      GrammarStatusView = require('./grammar-status-view');
      view = new GrammarStatusView(statusBar);
      return view.attach();
    }
  };

}).call(this);
