(function() {
  var path;

  path = require('path');

  module.exports = {
    configDefaults: {
      showOnStartup: true
    },
    activate: function() {
      atom.workspaceView.command('welcome:show-welcome-buffer', (function(_this) {
        return function() {
          return _this.show();
        };
      })(this));
      if (atom.config.get('welcome.showOnStartup')) {
        this.show();
        return atom.config.set('welcome.showOnStartup', false);
      }
    },
    show: function() {
      var welcomePath;
      welcomePath = path.resolve(__dirname, '..', 'docs', process.platform, 'welcome.md');
      return atom.workspaceView.open(welcomePath);
    }
  };

}).call(this);
